import process from 'node:process';
import cac from 'cac';
import { configName, getConfig, initConfig } from '@tailwindcss-mangle/config';
import { T as TailwindcssPatcher, g as getPatchOptions } from './shared/tailwindcss-patch.c6a99ac9.mjs';
import '@babel/types';
import 'node:path';
import 'node:fs';
import 'semver';
import 'node:fs/promises';
import 'resolve';
import '@babel/generator';
import '@babel/traverse';
import '@babel/parser';
import 'postcss';
import 'lilconfig';
import 'jiti';

function init() {
  const cwd = process.cwd();
  return initConfig(cwd);
}
const cli = cac();
cli.command("install", "patch install").action(() => {
  const twPatcher = new TailwindcssPatcher({
    patch: getPatchOptions()
  });
  twPatcher.patch();
});
cli.command("init").action(async () => {
  await init();
  console.log(`\u2728 ${configName}.config.ts initialized!`);
});
cli.command("extract").action(async () => {
  const { config } = await getConfig();
  if (config) {
    const twPatcher = new TailwindcssPatcher();
    const p = await twPatcher.extract(config.patch);
    console.log(`\u2728 tailwindcss-patch extract success! file path: ${p}`);
  }
});
cli.help();
cli.parse();
